// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import './governor.sol';
import './interfaces/dcip.sol';

import 'hardhat/console.sol';

contract DCIPGovernor is Governor {
    using Timers for Timers.BlockNumber;

    constructor(IDCIP _token) Governor(_token) {}

    function minVotingPeriod() public pure returns (uint256) {
        return 57600; // 2 days
    }

    function maxVotingPeriod() public pure returns (uint256) {
        return 806400; // 4 weeks
    }

    function proposalThreshold() public pure returns (uint256) {
        return 50000000000e9;
    }

    function setProposalTreshhold(uint256 _threshold) public onlyOwner returns (bool) {
        proposalTreshhold = _threshold;
        return true;
    }

    function propose(
        string memory title,
        string memory description,
        uint256 fundAllocation,
        uint64 votingPeriod
    ) public override returns (uint256) {
        require(
            getVotes(_msgSender()) >= proposalThreshold(),
            'GovernorCompatibilityBravo: proposer votes below proposal threshold'
        );
        // TODO
        // Check if not blocked
        // Check if enough balance
        // CanHaveOnlyXActiveProposals??
        return super.propose(title, description, fundAllocation, votingPeriod);
    }

    /**
     * @dev If amount of votes cast passes the threshold limit.
     */
    function quorumReached(uint256 proposalId) public view virtual override returns (bool) {
        ProposalVote storage proposalVote = proposalVotes[proposalId];

        return votesForQuorum <= proposalVote.votesFor + proposalVote.votesAgainst;
    }

    function setVotesForQuorum(uint256 nrOfTokens) public virtual override onlyOwner returns (uint256) {
        require(nrOfTokens > 1000000000000000, 'At least 1.000.000 DCIP is required');
        votesForQuorum = nrOfTokens;
        return votesForQuorum;
    }

    function getVotes(address voter) public virtual override returns (uint256) {
        return token.balanceOf(voter);
    }

    function hasVoted(address account, uint256 proposalId) public view returns (bool) {
        return proposalVotes[proposalId].hasVoted[account];
    }

    /**
     * @dev Is the proposal successful or not.
     */
    function voteSucceeded(uint256 proposalId) public view override returns (bool) {
        ProposalVote storage proposalVote = proposalVotes[proposalId];

        return proposalVote.votesFor > proposalVote.votesAgainst;
    }

    function state(uint256 proposalId) public view virtual override returns (ProposalState) {
        Proposal memory proposal = proposals[proposalId];

        if (proposal.executed) {
            return ProposalState.Executed;
        } else if (proposal.invalidated) {
            return ProposalState.Invalidated;
        } else if (proposal.voteStart.isPending()) {
            return ProposalState.Pending;
        } else if (proposal.voteEnd.isPending()) {
            return ProposalState.Active;
        } else if (proposal.voteEnd.isExpired()) {
            return
                quorumReached(proposalId) && voteSucceeded(proposalId)
                    ? ProposalState.Succeeded
                    : ProposalState.Defeated;
        } else {
            revert('Governor: unknown proposal id');
        }
    }

    function _countVote(
        uint256 proposalId,
        address account,
        bool support,
        uint256 weight
    ) internal virtual override {
        ProposalVote storage pVote = proposalVotes[proposalId];
        require(pVote.hasVoted[account] == false, 'Vote has already been cast!');
        require(weight >= 1, 'Invalid vote weight');
        if (support == true) {
            pVote.votesFor += weight;
        } else {
            pVote.votesAgainst += weight;
        }
        pVote.hasVoted[account] = true;
        // console.log(
        //     'Account: %s proposalId: %s hasVoted: %s weight: %s votedFor: %s',
        //     account,
        //     proposalId,
        //     pVote.hasVoted[account],
        //     weight,
        //     support
        // );
        emit VoteCast(account, proposalId, support, weight);
    }

    function invalidateProposal() public onlyOwner returns (bool) {
        // TODO
        return true;
    }

    /**
     * @dev See {IGovernor-proposalDeadline}.
     */
    function proposalDeadline(uint256 proposalId) public view returns (uint256) {
        return proposals[proposalId].voteEnd.getDeadline();
    }
}

