//SPDX-License-Identifier: MIT

pragma solidity ^0.8.4;

import './interfaces/governor.sol';
import './libraries/ownable.sol';
import './interfaces/dcip.sol';
import './libraries/timers.sol';
import '@openzeppelin/contracts/utils/math/SafeCast.sol';

import 'hardhat/console.sol';

abstract contract Governor is Ownable, IGovernor {
    using SafeCast for uint256;
    using Timers for Timers.BlockNumber;

    IDCIP public token;

    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => ProposalVote) public proposalVotes;
    uint256 proposalTreshhold;
    uint256 public proposalCount;
    uint256 public votesForQuorum;

    constructor(IDCIP _tokenAddress) {
        token = _tokenAddress;
        proposalCount = 0;
        votesForQuorum = 500000000000; /// Approxx 100 euros at current value
    }

    function propose(
        string memory title,
        string memory description,
        uint256 fundAllocation,
        uint64 votingPeriod
    ) public virtual returns (uint256) {
        // TODO: Build in protection against reentrancy
        // require(bytes(description) > 10, 'Description seems empty or too small');

        uint64 voteStart = block.number.toUint64();
        uint64 deadline = voteStart + votingPeriod;
        uint256 proposalId = proposalCount;

        Proposal memory p = Proposal({
            id: proposalCount,
            proposer: msg.sender,
            title: title,
            description: description,
            fundAllocation: fundAllocation,
            voteStart: Timers.BlockNumber(voteStart),
            voteEnd: Timers.BlockNumber(deadline),
            invalidated: false,
            executed: false
        });

        emit ProposalCreated(proposalId, title, fundAllocation);

        proposals[proposalId] = p;
        ProposalVote storage pVote = proposalVotes[proposalId];
        pVote.votesAgainst = 0;
        pVote.votesFor = 0;

        _countVote(proposalId, _msgSender(), true, getVotes(_msgSender()));
        proposalCount++;

        // TOOD: Auto vote on proposals
        return proposalId;
    }

    function getVotes(address voter) public virtual returns (uint256) {}

    /**
     * @dev See {IGovernor-castVote}.
     */
    function castVote(uint256 proposalId, bool support) public virtual returns (uint256) {
        address voter = _msgSender();
        return _castVote(proposalId, voter, support);
    }

    /**
     * @dev Internal vote casting mechanism: Check that the vote is pending, that it has not been cast yet, retrieve
     * voting weight using {IGovernor-getVotes} and call the {_countVote} internal function.
     *
     * Emits a {IGovernor-VoteCast} event.
     */
    function _castVote(
        uint256 proposalId,
        address account,
        bool support
    ) internal virtual returns (uint256) {
        require(state(proposalId) == ProposalState.Active, 'Governor: vote not currently active');

        uint256 weight = getVotes(account);
        _countVote(proposalId, account, support, weight);
        return weight;
    }

    function setVotesForQuorum(uint256 nrOfTokens) public virtual returns (uint256);

    function state(uint256 proposalId) public view virtual returns (ProposalState);

    /**
     * @dev Amount of votes already cast passes the threshold limit.
     */
    function quorumReached(uint256 proposalId) public view virtual returns (bool);

    /**
     * @dev Is the proposal successful or not.
     */
    function voteSucceeded(uint256 proposalId) public view virtual returns (bool);

    /**
     * @dev Register a vote with a given support and voting weight.
     *
     * Note: Support is generic and can represent various things depending on the voting system used.
     */
    function _countVote(
        uint256 proposalId,
        address account,
        bool support,
        uint256 weight
    ) internal virtual;
}

